function timer(e,t){if(e<0)e=0;Seconds=e;Hours=Math.floor(Seconds/3600);Seconds-=Hours*3600;if(Hours<10){Hours="0"+Hours}Minutes=Math.floor(Seconds/60);Seconds-=Minutes*60;if(Minutes<10){Minutes="0"+Minutes}Seconds=Math.floor(Seconds);if(Seconds<10){Seconds="0"+Seconds}document.getElementById(t).innerHTML=Hours+":"+Minutes+":"+Seconds;if(e<=0){clearTimeout(this)}else{e-=1;setTimeout(function(){timer(e,t)},1e3)}}function timer_system(e,t){if(e<0)e=0;Seconds=e;Days=Math.floor(Seconds/(24*3600));Seconds-=Days*24*3600;Hours=Math.floor(Seconds/3600);Seconds-=Hours*3600;if(Hours<10){Hours="0"+Hours}Minutes=Math.floor(Seconds/60);Seconds-=Minutes*60;if(Minutes<10){Minutes="0"+Minutes}Seconds=Math.floor(Seconds);if(Seconds<10){Seconds="0"+Seconds}document.getElementById(t).innerHTML=Days+" дней "+Hours+":"+Minutes+":"+Seconds;if(e<=0){clearTimeout(this)}else{e-=1;setTimeout(function(){timer_system(e,t)},1e3)}}function timer_rinok(e,t){Seconds=e;Days=Math.floor(Seconds/(24*3600));Seconds-=Days*24*3600;Hours=Math.floor(Seconds/3600);Seconds-=Hours*3600;if(Hours<10){Hours="0"+Hours}Minutes=Math.floor(Seconds/60);Seconds-=Minutes*60;if(Minutes<10){Minutes="0"+Minutes}Seconds=Math.floor(Seconds);if(Seconds<10){Seconds="0"+Seconds}document.getElementById(t).innerHTML=Days+" дней "+Hours+":"+Minutes+":"+Seconds;e+=1;setTimeout(function(){timer_rinok(e,t)},1e3)}function server_time(){var e=document.getElementById("Server_hours").value;var t=document.getElementById("Server_minutes").value;var n=document.getElementById("Server_seconds").value;if(n<59){n++;if(n<10){n="0"+n}}else{n=0;if(n<10){n="0"+n}if(t<59){t++;if(t<10){t="0"+t}}else{t=0;if(t<10){t="0"+t}if(e<23){e++;if(e<10){e="0"+e}}else{e=0;if(e<10){e="0"+e}}}}document.getElementById("Server_hours").value=e;document.getElementById("Server_minutes").value=t;document.getElementById("Server_seconds").value=n;var r=e+":"+t+":"+n;document.getElementById("server_time").innerHTML=r;setTimeout(function(){server_time()},1e3)}